function JSONtoTextGrid(n){return tg_str='File type = "ooTextFile"\nObject class = "TextGrid"\n\nxmin = '+n.item[0].xmin+"\nxmax = "+n.item[0].xmax+"\ntiers? <exists>\nsize = "+n.item.length+"\nitem []:\n",n.item.forEach(function(n,t){tg_str+="    item ["+(t+1)+']:\n        class = "'+n["class"]+'"\n        name = "'+n.name+'"\n        xmin = '+n.xmin+"\n        xmax = "+n.xmax+"\n","IntervalTier"==n["class"]?(tg_str+="        intervals: size = "+n.intervals.length+"\n",n.intervals.forEach(function(n,t){tg_str+="        intervals ["+(t+1)+"]:\n            xmin = "+n.xmin+"\n            xmax = "+n.xmax+'\n            text = "'+n.text+'"\n'})):"TextTier"==n["class"]?(tg_str+="        points: size = "+n.points.length+"\n",n.points.length>0&&n.points.forEach(function(n,t){tg_str+="        points ["+(t+1)+"]:\n            number = "+n.number+'\n            mark = "'+n.mark+'"\n'})):console.log("Tier class unknown!")}),tg_str}